"""Test PCR primer designer to debug issues"""

from pcr_primer_designer import PCRPrimerOptimizer

# Your sequence from the screenshot
template = ""

print(f"Template length: {len(template)} bp")
print()

optimizer = PCRPrimerOptimizer()

# Test with your settings
print("Testing with GUI default settings:")
print("Forward: 0-50")
print("Reverse: auto (should be ~5768-5818)")
print()

try:
    results = optimizer.design_pcr_primers(
        template,
        fwd_start=0,
        fwd_end=50,
        rev_start=None,  # Will default to len-50
        rev_end=None,    # Will default to len
        min_primer_length=18,
        max_primer_length=25,
        target_tm=60.0,
        step=5,
        max_results=10
    )
    
    print(f"\n✓ Found {len(results)} primer pairs!")
    
    if results:
        print("\nTop result:")
        r = results[0]
        print(f"  Forward position: {r['fwd_pos']}")
        print(f"  Reverse position: {r['rev_pos']}")
        print(f"  Amplicon size: {r['amplicon_size']} bp")
        print(f"  Score: {r['score']:.1f}")
        print(f"  Forward primer: {r['primers']['forward']}")
        print(f"  Reverse primer: {r['primers']['reverse']}")
        print(f"  Forward Tm: {r['validations']['forward']['metrics']['tm']:.1f}°C")
        print(f"  Reverse Tm: {r['validations']['reverse']['metrics']['tm']:.1f}°C")
    
except Exception as e:
    print(f"\n✗ Error: {e}")
    import traceback
    traceback.print_exc()
